mapper.select:
select dept_id
from sys_dept
where parent_id = #{deptId}

mapper.update:
update sys_dept
set status = #{status}
where dept_id in (
    select dept_id
    from (
             select dept_id
             from sys_dept
             where sys_dept = #{deptId}
                or parent_id = #{deptId}
                or parent_id in
                   (select dept_id from (select dept_id from sys_dept where parent_id = #{deptId}) temp)
         ) a
)
