mapper.select:
select
menu_id, menu_name, parent_id, order_num, path, component, is_frame, is_cache, menu_type, visible, status,
perms, icon, create_by, create_time, update_by, update_time, remark
from sys_menu


        and menu_id = #{menuId}


        and menu_name = #{menuName}


        and parent_id = #{parentId}


        and order_num = #{orderNum}


        and path = #{path}


        and component = #{component}


        and is_frame = #{isFrame}


        and is_cache = #{isCache}


        and menu_type = #{menuType}


        and visible = #{visible}


        and status = #{status}


        and perms = #{perms}


        and icon = #{icon}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_menu


        and menu_id = #{menuId}


        and menu_name = #{menuName}


        and parent_id = #{parentId}


        and order_num = #{orderNum}


        and path = #{path}


        and component = #{component}


        and is_frame = #{isFrame}


        and is_cache = #{isCache}


        and menu_type = #{menuType}


        and visible = #{visible}


        and status = #{status}


        and perms = #{perms}


        and icon = #{icon}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, component = #{component}, is_frame = #{isFrame}, is_cache = #{isCache}, menu_type = #{menuType}, visible = #{visible}, status = #{status}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}
