mapper.select:
select
role_id, role_name, role_key, role_sort, data_scope, menu_check_strictly, dept_check_strictly, status, del_flag,
create_by, create_time, update_by, update_time, remark
from sys_role
where del_flag = 0

    and role_id = #{roleId}


    and role_name like concat('%',#{roleName},'%')


    and role_key like concat('%',#{roleKey},'%')


    and role_sort = #{roleSort}


    and data_scope = #{dataScope}


    and menu_check_strictly = #{menuCheckStrictly}


    and dept_check_strictly = #{deptCheckStrictly}


    and status = #{status}


    and del_flag = #{delFlag}


    and create_by = #{createBy}


    and create_time = #{createTime}


    and update_by = #{updateBy}


    and update_time = #{updateTime}


    and remark = #{remark}

limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_role
where del_flag = 0

    and role_id = #{roleId}


    and role_name like concat('%',#{roleName},'%')


    and role_key like concat('%',#{roleKey},'%')


    and role_sort = #{roleSort}


    and data_scope = #{dataScope}


    and menu_check_strictly = #{menuCheckStrictly}


    and dept_check_strictly = #{deptCheckStrictly}


    and status = #{status}


    and del_flag = #{delFlag}


    and create_by = #{createBy}


    and create_time = #{createTime}


    and update_by = #{updateBy}


    and update_time = #{updateTime}


    and remark = #{remark}
