mapper.select:
select
building_id, building_name, building_code, building_acreage, community_id, create_by, create_time, update_by,
update_time, remark
from zy_building

    
        and building_id = #{buildingId}
    
    
        and building_name = #{buildingName}
    
    
        and building_code = #{buildingCode}
    
    
        and building_acreage = #{buildingAcreage}
    
    
        and community_id = #{communityId}
    
    
        and create_by = #{createBy}
    
    
        and create_time = #{createTime}
    
    
        and update_by = #{updateBy}
    
    
        and update_time = #{updateTime}
    
    
        and remark = #{remark}
    

limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from zy_building

    
        and building_id = #{buildingId}
    
    
        and building_name = #{buildingName}
    
    
        and building_code = #{buildingCode}
    
    
        and building_acreage = #{buildingAcreage}
    
    
        and community_id = #{communityId}
    
    
        and create_by = #{createBy}
    
    
        and create_time = #{createTime}
    
    
        and update_by = #{updateBy}
    
    
        and update_time = #{updateTime}
    
    
        and remark = #{remark}
