mapper.select:
select
community_id, community_name, community_code, community_provence_code, community_city_code, community_town_code,
community_detailed_address, community_longitude, community_latitude, dept_id, community_sort, create_by,
create_time, update_by, update_time, remark
from zy_community

    
        and community_id = #{communityId}
    
    
        and community_name = #{communityName}
    
    
        and community_code = #{communityCode}
    
    
        and community_provence_code = #{communityProvenceCode}
    
    
        and community_city_code = #{communityCityCode}
    
    
        and community_town_code = #{communityTownCode}
    
    
        and community_detailed_address = #{communityDetailedAddress}
    
    
        and community_longitude = #{communityLongitude}
    
    
        and community_latitude = #{communityLatitude}
    
    
        and dept_id = #{deptId}
    
    
        and community_sort = #{communitySort}
    
    
        and create_by = #{createBy}
    
    
        and create_time = #{createTime}
    
    
        and update_by = #{updateBy}
    
    
        and update_time = #{updateTime}
    
    
        and remark = #{remark}
    

limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from zy_community

    
        and community_id = #{zyCommunity.communityId}
    
    
        and community_name like concat('%',#{zyCommunity.communityName},'%')
    
    
        and community_code like concat('%',#{zyCommunity.communityCode},'%')
    
    
        and community_provence_code = #{zyCommunity.communityProvenceCode}
    
    
        and community_city_code = #{zyCommunity.communityCityCode}
    
    
        and community_town_code = #{zyCommunity.communityTownCode}
    
    
        and community_detailed_address = #{zyCommunity.communityDetailedAddress}
    
    
        and community_longitude = #{zyCommunity.communityLongitude}
    
    
        and community_latitude = #{zyCommunity.communityLatitude}
    
    
        and dept_id = #{zyCommunity.deptId}
    
    
        and community_sort = #{zyCommunity.communitySort}
    
    
        and create_by = #{zyCommunity.createBy}
    
    
        and create_time = #{zyCommunity.createTime}
    
    
        and update_by = #{zyCommunity.updateBy}
    
    
        and update_time = #{zyCommunity.updateTime}
    
    
        and remark = #{zyCommunity.remark}
