mapper.select:
select *
from zy_community
where community_provence_code = #{communityProvenceCode}
and community_city_code = #{communityCityCode}
and community_town_code = #{communityTownCode}
and community_detailed_address = #{communityDetailedAddress}

    and community_id != #{communityId}

select *,(select name from sys_area a1 where a1.`code` = zc.community_provence_code) communityProvenceName, (select name from sys_area a1 where a1.`code` = zc.community_city_code) communityCityName, (select name from sys_area a1 where a1.`code` = zc.community_town_code) communityTownName from zy_community zc
where zc.community_id in
#{id}
