mapper.select:
select
interaction_id, community_id, create_by, update_by, create_time, update_time, content, del_flag, remark, user_id
from zy_community_interaction


        and interaction_id = #{interactionId}


        and community_id = #{communityId}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and content = #{content}


        and del_flag = #{delFlag}


        and remark = #{remark}


        and user_id = #{userId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from zy_community_interaction


        and interaction_id = #{interactionId}


        and community_id = #{communityId}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and content = #{content}


        and del_flag = #{delFlag}


        and remark = #{remark}


        and user_id = #{userId}

select ci.*,o.owner_nickname,o.owner_real_name,o.owner_phone_number,o.owner_portrait from zy_community_interaction ci left join zy_owner o on ci.user_id=o.owner_id where del_flag = 0 and community_id = #{dto.communityId}
and owner_nickname like concat('%',#{dto.ownerNickname},'%')
and owner_real_name like concat('%',#{dto.ownerRealName},'%')
and owner_phone_number like concat('%',#{dto.ownerPhoneNumber},'%')
order by (select count(1) from zy_comment where interaction_id = ci.interaction_id) desc,ci.create_time desc
limit #{pageable.index},#{pageable.pageSize}

mapper.update:
update zy_community_interaction set del_flag = 1
where interaction_id in

    #{id}
