mapper.select:
select
owner_park_id, park_id, owner_id, park_status, create_by, create_time, update_by, update_time, remark
from zy_owner_park


        and owner_park_id = #{ownerParkId}


        and park_id = #{parkId}


        and owner_id = #{ownerId}


        and park_status = #{parkStatus}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}
