mapper.select:
select
owner_park_id, park_id, owner_id, park_status, create_by, create_time, update_by, update_time, remark
from zy_owner_park

    
        and owner_park_id = #{ownerParkId}
    
    
        and park_id = #{parkId}
    
    
        and owner_id = #{ownerId}
    
    
        and park_status = #{parkStatus}
    
    
        and create_by = #{createBy}
    
    
        and create_time = #{createTime}
    
    
        and update_by = #{updateBy}
    
    
        and update_time = #{updateTime}
    
    
        and remark = #{remark}
    

limit #{pageable.offset}, #{pageable.pageSize}

select zc.community_name,zp.park_code,zp.park_type,zop.park_owner_status,zp.park_is_public,zop.create_by,zp.park_status,zop.remark from zy_owner_park zop LEFT JOIN zy_park zp on zop.park_id = zp.park_id LEFT JOIN zy_community zc on zp.community_id = zc.community_id
and zop.park_owner_status = #{zyOwnerParkDto.parkOwnerStatus}
order by zop.create_time desc
limit #{zyOwnerParkDto.pageable.index},#{zyOwnerParkDto.pageable.pageSize}
